package managedbeans;

/**
 *  
 * @author Asier Gonzalez
 */
import entitybeans.Files;
import entitybeans.Filetype;
import entitybeans.Projects;
import java.io.File;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.util.ArrayList;
import java.util.Collection;
import javax.ejb.EJB;
 
import javax.faces.application.FacesMessage;
import javax.faces.bean.ManagedBean;
import javax.faces.bean.ManagedProperty;
import javax.faces.context.FacesContext;
 
import org.primefaces.event.FileUploadEvent;
import sessionbeans.FilesFacade;
import sessionbeans.ProjectSessionFacade;
  
/**
 * This is a managed bean that uses <a href="http://www.primefaces.org/showcase/">PrimeFaces</a> API to upload the local file(s) selected by the user to the <i>/home/vmuser/CPI/uploads</i> directory on the server and adds it to the database. 
 */
@ManagedBean
public class FileUploadBean {
   /**
    * Full path to the directory where the uploaded file will be stored
    */
    private String destination="/home/vmuser/CPI/uploads/";
    
    /**
     * Enterprise JavaBean that gives access to the methods to communicate with the <i>Files</i> table in the database 
     */
    @EJB
    private FilesFacade filesFacade;
    
    /**
     * Enterprise JavaBean that gives access to the methods to communicate with the <i>Projects</i> table in the database
     */
    @EJB
    ProjectSessionFacade projectFacade;
    
    /**
     * Session scoped bean that stores information about the connected user and selected project
     */
    
    @ManagedProperty(value = "#{utilityBean}")
    private UtilityBean utilityBean;
 
    /**
     * This is the function that is launched when the <i>Upload dataset</i> button in the project page is pressed.                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 
     * @param event The signal meaning that <i>Upload dataset</i> has been pressed
     */
    public void upload(FileUploadEvent event) {  
        System.err.println("FUB.upload entered");
        FacesMessage msg = new FacesMessage("Success! ", event.getFile().getFileName() + " is uploaded.");  
        FacesContext.getCurrentInstance().addMessage(null, msg);
        // Do what you want with the file        
        try {
            copyFile(event.getFile().getFileName(), event.getFile().getInputstream());
        } catch (IOException e) {
            e.printStackTrace();
        }
 
    }  
 
    /**
     * It reads the file in blocks of 1 kB and copies them into the destination file.
     * @param fileName The destination filename
     * @param in The information
     */
    public void copyFile(String fileName, InputStream in) {
        System.err.println("FUB.copyFile entered");   
        try {
              
              
                // write the inputStream to a FileOutputStream
                OutputStream out = new FileOutputStream(new File(destination + fileName));
              
                int read = 0;
                byte[] bytes = new byte[1024];
              
                while ((read = in.read(bytes)) != -1) {
                    out.write(bytes, 0, read);
                }
              
                in.close();
                out.flush();
                out.close();
              
                System.out.println("New file created!");
                addFileToDB(fileName);
                } catch (IOException e) {
                System.out.println(e.getMessage());
                }
    }
    
    
    /**
     * Adds the uploaded files to the database
     * @param fileName Name of the uploaded file
     */
    public void addFileToDB(String fileName){
        Files uploadedFile = new Files();
        Collection<Files> projectFiles = utilityBean.getSelectedProject().getFilesCollection();
                
        ArrayList<Projects> fileProject = new ArrayList(1);
        System.out.println("Project " + utilityBean.getSelectedProject().getProjectname());
        fileProject.add(utilityBean.getSelectedProject());

        uploadedFile.setPath(destination + fileName);
        uploadedFile.setDisplayname(fileName);
        uploadedFile.setDescription("User uploaded file");
        //All uploaded files will be considered raw files, filetype 7
        uploadedFile.setFiletype(new Filetype(1));
        uploadedFile.setProjectsCollection(fileProject);

        //Persist file object - must be done before adding to projects! 
        filesFacade.create(uploadedFile);

        //Update project with new file - must be done after persistence to avoid file duplication
        projectFiles.add(uploadedFile);
        utilityBean.getSelectedProject().setFilesCollection(projectFiles);
        projectFacade.edit(utilityBean.getSelectedProject());
    }
    
     /**
      * Gets the utility Bean
     * @param utilityBean The utilityBean to set
     */
    public void setUtilityBean(UtilityBean utilityBean) {
        this.utilityBean = utilityBean;
    }
}
